// App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [showForm, setShowForm] = useState(false);
  const [theme, setTheme] = useState('light');
  const [ipoList, setIpoList] = useState([]);
  const [formData, setFormData] = useState({
    companyName: '',
    priceBand: '',
    openDate: '',
    closeDate: '',
    issueSize: '',
  });

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    document.body.className = newTheme;
    setTheme(newTheme);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (
      !formData.companyName ||
      !formData.priceBand ||
      !formData.openDate ||
      !formData.closeDate ||
      !formData.issueSize
    ) {
      alert('Please fill out all fields.');
      return;
    }

    setIpoList([...ipoList, formData]);
    setFormData({
      companyName: '',
      priceBand: '',
      openDate: '',
      closeDate: '',
      issueSize: '',
    });
    setShowForm(false);
  };

  return (
    <div className={`app ${theme}`}>
      <header>
        IPO Dashboard
        <div>
          <button className="toggle-btn" onClick={toggleTheme}>
            {theme === 'light' ? 'Dark' : 'Light'} Mode
          </button>
          <button className="add-btn" onClick={() => setShowForm(true)}>
            Add IPO
          </button>
        </div>
      </header>

      <div className="ipo-list">
        {ipoList.map((ipo, index) => (
          <div className="ipo-card" key={index}>
            <h3>{ipo.companyName}</h3>
            <p><strong>Price Band:</strong> {ipo.priceBand}</p>
            <p><strong>Open:</strong> {ipo.openDate}</p>
            <p><strong>Close:</strong> {ipo.closeDate}</p>
            <p><strong>Issue Size:</strong> {ipo.issueSize}</p>
          </div>
        ))}
      </div>

      {showForm && (
        <div className="form-popup active">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="companyName"
              value={formData.companyName}
              onChange={handleChange}
              placeholder="Company Name"
            />
            <input
              type="text"
              name="priceBand"
              value={formData.priceBand}
              onChange={handleChange}
              placeholder="Price Band"
            />
            <input
              type="date"
              name="openDate"
              value={formData.openDate}
              onChange={handleChange}
              placeholder="Open Date"
            />
            <input
              type="date"
              name="closeDate"
              value={formData.closeDate}
              onChange={handleChange}
              placeholder="Close Date"
            />
            <input
              type="text"
              name="issueSize"
              value={formData.issueSize}
              onChange={handleChange}
              placeholder="Issue Size"
            />
            <button type="submit" className="submit-btn">Submit</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
