// âœ… App.js
import React, { useState, useEffect } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [ipos, setIPOs] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [date, setDate] = useState("");
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    loadIPOs();
  }, []);

  const loadIPOs = async () => {
    try {
      const data = await fetchIPOs();
      setIPOs(data);
    } catch (err) {
      alert("Error fetching IPOs");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !price || !date) {
      alert("Please fill in all fields.");
      return;
    }

    try {
      const newIPO = {
        company_name: name,
        issue_price: price,
        issue_date: date,
      };
      await createIPO(newIPO);
      setName("");
      setPrice("");
      setDate("");
      setShowForm(false);
      loadIPOs();
      alert("IPO created successfully!");
    } catch (err) {
      alert("Failed to submit IPO");
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>ðŸ“ˆ IPO Tracker</h1>
        <div>
          <button className="add-btn" onClick={() => setShowForm(!showForm)}>âž• Add IPO</button>
          <button className="theme-btn">ðŸŒž</button>
        </div>
      </header>

      {showForm && (
        <form className="ipo-form" onSubmit={handleSubmit}>
          <h2>Add New IPO</h2>
          <input
            type="text"
            placeholder="Company Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Issue Price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
          <button type="submit">Submit IPO</button>
        </form>
      )}

      <section className="ipo-list">
        {ipos.length === 0 ? (
          <p>No IPOs found.</p>
        ) : (
          ipos.map((ipo, index) => (
            <div key={index} className="ipo-card">
              <h3>{ipo.company_name}</h3>
              <p>ðŸ’° Issue Price: â‚¹{ipo.issue_price}</p>
              <p>ðŸ“… Date: {ipo.issue_date}</p>
            </div>
          ))
        )}
      </section>
    </div>
  );
}

export default App;
