import React, { useEffect, useState } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [ipoList, setIpoList] = useState([]);
  const [formVisible, setFormVisible] = useState(false);
  const [theme, setTheme] = useState("light");
  const [formData, setFormData] = useState({
    name: "",
    company: "",
    price: "",
  });
  const [error, setError] = useState("");

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  useEffect(() => {
    async function loadData() {
      try {
        const ipos = await fetchIPOs();
        setIpoList(ipos);
      } catch (e) {
        console.error(e);
        setError("Could not fetch IPOs.");
      }
    }
    loadData();
  }, []);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  const toggleForm = () => {
    setFormVisible(!formVisible);
    setError("");
    setFormData({ name: "", company: "", price: "" });
  };

  const handleInput = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.company || !formData.price) {
      setError("All fields are required!");
      return;
    }

    try {
      const newIPO = await createIPO(formData);
      setIpoList([newIPO, ...ipoList]);
      toggleForm();
    } catch (e) {
      setError("Failed to create IPO.");
    }
  };

  return (
    <div className="App">
      <header>
        <span>📈 IPO Dashboard</span>
        <div>
          <button className="toggle-btn" onClick={toggleTheme}>
            {theme === "light" ? "🌙 Dark Mode" : "☀️ Light Mode"}
          </button>
          <button className="add-btn" onClick={toggleForm}>
            ➕ Add IPO
          </button>
        </div>
      </header>

      <div className="ipo-list">
        {ipoList.length === 0 ? (
          <p style={{ fontSize: "18px" }}>No IPOs available yet.</p>
        ) : (
          ipoList.map((ipo, index) => (
            <div className="ipo-card" key={index}>
              <h3>{ipo.name}</h3>
              <p><strong>Company:</strong> {ipo.company}</p>
              <p><strong>Price:</strong> ₹{ipo.price}</p>
            </div>
          ))
        )}
      </div>

      <div className={`form-popup ${formVisible ? "active" : ""}`}>
        <form onSubmit={handleSubmit}>
          <h2>Add New IPO</h2>
          <input
            type="text"
            name="name"
            placeholder="IPO Name"
            value={formData.name}
            onChange={handleInput}
          />
          <input
            type="text"
            name="company"
            placeholder="Company Name"
            value={formData.company}
            onChange={handleInput}
          />
          <input
            type="number"
            name="price"
            placeholder="Price (₹)"
            value={formData.price}
            onChange={handleInput}
          />
          {error && <p className="error-msg">{error}</p>}
          <button className="submit-btn" type="submit">
            Submit IPO 🚀
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
