import React, { useEffect, useState } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [ipos, setIpos] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [newIpo, setNewIpo] = useState({
    company_name: "",
    issue_date: "",
    price: "",
    lot_size: "",
    status: "",
  });

  // Fetch IPO data
  useEffect(() => {
    async function loadData() {
      try {
        const data = await fetchIPOs();
        setIpos(data);
      } catch (error) {
        console.error(error);
      }
    }
    loadData();
  }, []);

  // Form handlers
  function handleChange(e) {
    setNewIpo({ ...newIpo, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const addedIPO = await createIPO(newIpo);
      setIpos([...ipos, addedIPO]);
      setNewIpo({
        company_name: "",
        issue_date: "",
        price: "",
        lot_size: "",
        status: "",
      });
      setShowForm(false);
    } catch (error) {
      console.error(error);
    }
  }

  // Dark/Light toggle
  function toggleTheme() {
    setDarkMode(!darkMode);
  }

  return (
    <div className={`app-container ${darkMode ? "dark-mode" : "light-mode"}`}>
      <header className="header">
        <h1 className="title">IPO Dashboard</h1>
        <div>
          <button className="theme-toggle" onClick={toggleTheme}>
            {darkMode ? "☀️ Light Mode" : "🌙 Dark Mode"}
          </button>
          <button className="add-btn" onClick={() => setShowForm(true)}>
            + Add IPO
          </button>
        </div>
      </header>

      <div className="ipo-list">
        {ipos.map((ipo) => (
          <div className="ipo-card" key={ipo.id}>
            <span className={`status-badge ${ipo.status.toLowerCase()}`}>
              {ipo.status}
            </span>
            <h2>{ipo.company_name}</h2>
            <p><strong>Issue Date:</strong> {ipo.issue_date}</p>
            <p><strong>Price:</strong> ₹{ipo.price}</p>
            <p><strong>Lot Size:</strong> {ipo.lot_size}</p>
          </div>
        ))}
      </div>

      {showForm && (
        <div className="modal-overlay" onClick={() => setShowForm(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>Add New IPO</h2>
            <form onSubmit={handleSubmit} className="ipo-form">
              <input
                type="text"
                name="company_name"
                placeholder="Company Name"
                value={newIpo.company_name}
                onChange={handleChange}
                required
              />
              <input
                type="date"
                name="issue_date"
                value={newIpo.issue_date}
                onChange={handleChange}
                required
              />
              <input
                type="number"
                name="price"
                placeholder="Price"
                value={newIpo.price}
                onChange={handleChange}
                required
              />
              <input
                type="number"
                name="lot_size"
                placeholder="Lot Size"
                value={newIpo.lot_size}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="status"
                placeholder="Status (LIVE/UPCOMING/CLOSED)"
                value={newIpo.status}
                onChange={handleChange}
                required
              />
              <button type="submit" className="submit-btn">
                Submit
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
