// App.js
import React, { useEffect, useState } from 'react';
import './App.css'; // ✅ Matches the filename exactly (uppercase A)
import { fetchIPOs, createIPO } from './api';

function App() {
  const [ipos, setIPOs] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newIPO, setNewIPO] = useState({
    company_name: '',
    issue_date: '',
    price: ''
  });
  const [darkMode, setDarkMode] = useState(true);

  useEffect(() => {
    document.body.className = darkMode ? 'dark' : 'light';
    loadIPOs();
  }, [darkMode]);

  const loadIPOs = async () => {
    try {
      const data = await fetchIPOs();
      setIPOs(data);
    } catch (error) {
      alert("Error loading IPOs: " + error.message);
    }
  };

  const handleInputChange = (e) => {
    setNewIPO({ ...newIPO, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createIPO(newIPO);
      setNewIPO({ company_name: '', issue_date: '', price: '' });
      setShowForm(false);
      loadIPOs();
    } catch (error) {
      alert("Error creating IPO: " + error.message);
    }
  };

  return (
    <div>
      <header>
        IPO Web Application
        <div>
          <button className="toggle-btn" onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
          <button className="add-btn" onClick={() => setShowForm(true)}>
            Add IPO
          </button>
        </div>
      </header>

      <div className="ipo-list">
        {ipos.map((ipo, index) => (
          <div className="ipo-card" key={index}>
            <h3>{ipo.company_name}</h3>
            <p>Issue Date: {ipo.issue_date}</p>
            <p>Price: ₹{ipo.price}</p>
          </div>
        ))}
      </div>

      <div className={`form-popup ${showForm ? 'active' : ''}`}>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="company_name"
            placeholder="Company Name"
            value={newIPO.company_name}
            onChange={handleInputChange}
            required
          />
          <input
            type="date"
            name="issue_date"
            value={newIPO.issue_date}
            onChange={handleInputChange}
            required
          />
          <input
            type="number"
            name="price"
            placeholder="Issue Price"
            value={newIPO.price}
            onChange={handleInputChange}
            required
          />
          <button className="submit-btn" type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default App;
