import React, { useState, useEffect } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [ipos, setIpos] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    price: "",
    date: "",
  });
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    loadIPOs();
  }, []);

  const loadIPOs = async () => {
    try {
      const data = await fetchIPOs();
      setIpos(data);
    } catch (err) {
      console.error("Error loading IPOs:", err);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, price, date } = formData;

    if (!name || !price || !date) {
      alert("Please fill in all IPO details.");
      return;
    }

    try {
      await createIPO(formData);
      alert("‚úÖ IPO Submitted Successfully!");
      setFormData({ name: "", price: "", date: "" });
      setShowForm(false);
      loadIPOs();
    } catch (err) {
      console.error("Submission failed:", err);
      alert("‚ùå Failed to submit IPO.");
    }
  };

  return (
    <div className="app">
      <nav className="navbar">
        <h1>üìà IPO Tracker</h1>
        <div className="btns">
          <button className="add-btn" onClick={() => setShowForm(!showForm)}>
            ‚ûï Add IPO
          </button>
        </div>
      </nav>

      {showForm && (
        <form className="ipo-form" onSubmit={handleSubmit}>
          <h2>Add New IPO</h2>
          <input
            type="text"
            name="name"
            value={formData.name}
            placeholder="Company Name"
            onChange={handleChange}
          />
          <input
            type="number"
            name="price"
            value={formData.price}
            placeholder="Issue Price"
            onChange={handleChange}
          />
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
          />
          <button type="submit">Submit IPO</button>
        </form>
      )}

      <div className="ipo-list">
        <h2>Upcoming IPOs</h2>
        {ipos.length === 0 ? (
          <p>No IPOs listed.</p>
        ) : (
          <ul>
            {ipos.map((ipo, index) => (
              <li key={index}>
                <strong>{ipo.name}</strong> ‚Äì ‚Çπ{ipo.price} on {ipo.date}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
