import React, { useState, useEffect } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [companyName, setCompanyName] = useState("");
  const [issueDate, setIssueDate] = useState("");
  const [issuePrice, setIssuePrice] = useState("");
  const [ipos, setIPOs] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    loadIPOs();
  }, []);

  const loadIPOs = async () => {
    try {
      const data = await fetchIPOs();
      setIPOs(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createIPO({ company_name: companyName, issue_date: issueDate, issue_price: issuePrice });
      setCompanyName("");
      setIssueDate("");
      setIssuePrice("");
      loadIPOs();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className={darkMode ? "app dark" : "app light"}>
      <header className="header">
        <h1>IPO Web Application</h1>
        <div>
          <button className="toggle-btn" onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
          <button className="toggle-btn add-btn" onClick={() => setShowForm(!showForm)}>
            {showForm ? "Close" : "Add IPO"}
          </button>
        </div>
      </header>

      {showForm && (
        <form className="ipo-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Company Name"
            value={companyName}
            onChange={(e) => setCompanyName(e.target.value)}
            required
          />
          <input
            type="date"
            value={issueDate}
            onChange={(e) => setIssueDate(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Issue Price"
            value={issuePrice}
            onChange={(e) => setIssuePrice(e.target.value)}
            required
          />
          <button type="submit">Submit</button>
        </form>
      )}

      <div className="ipo-list">
        {ipos.map((ipo, index) => (
          <div className="ipo-card" key={index}>
            <h3>{ipo.company_name}</h3>
            <p>Date: {ipo.issue_date}</p>
            <p>Price: â‚¹{ipo.issue_price}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
