const API_BASE = "http://localhost:5000/api/ipos"; // Change if your backend URL is different

// Fetch all IPOs
export const fetchIPOs = async () => {
  const response = await fetch(API_BASE);
  if (!response.ok) {
    throw new Error("Failed to fetch IPOs");
  }
  return await response.json();
};

// Create a new IPO
export const createIPO = async (ipoData) => {
  const response = await fetch(API_BASE, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(ipoData),
  });

  if (!response.ok) {
    throw new Error("Failed to create IPO");
  }

  return await response.json();
};
