// App.js
import React, { useEffect, useState } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

export default function App() {
  const [ipos, setIpos] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    price: "",
    issued_date: "",
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadIPOs();
  }, []);

  async function loadIPOs() {
    try {
      const data = await fetchIPOs();
      setIpos(data);
    } catch (err) {
      console.error("Failed to fetch IPOs:", err);
    }
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!formData.name || !formData.price || !formData.issued_date) return;

    try {
      setLoading(true);
      await createIPO(formData);
      setFormData({ name: "", price: "", issued_date: "" });
      loadIPOs();
    } catch (err) {
      console.error("Submit error:", err);
    } finally {
      setLoading(false);
    }
  }

  function handleChange(e) {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  }

  return (
    <div className="container">
      <h1 className="title">ðŸ“ˆ IPO Submission Portal</h1>
      <form className="ipo-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Company Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="issued_date"
          placeholder="Issued Date"
          value={formData.issued_date}
          onChange={handleChange}
          required
        />
        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Submitting..." : "Submit IPO"}
        </button>
      </form>

      <div className="ipo-list">
        <h2>ðŸ“‹ IPO Listings</h2>
        <ul>
          {ipos.map((ipo, index) => (
            <li key={index} className="ipo-card">
              <strong>{ipo.name}</strong>
              <span>ðŸ’° â‚¹{ipo.price}</span>
              <span>ðŸ—“ {ipo.issued_date}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
