// App.js

import React, { useEffect, useState } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [ipoList, setIpoList] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [theme, setTheme] = useState("light");
  const [formData, setFormData] = useState({
    company: "",
    price: "",
    date: "",
  });

  useEffect(() => {
    loadIPOs();
  }, []);

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  const loadIPOs = async () => {
    try {
      const data = await fetchIPOs();
      setIpoList(data);
    } catch (err) {
      console.error("Failed to load IPOs:", err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.company || !formData.price || !formData.date) return;

    try {
      await createIPO(formData);
      loadIPOs();
      setShowForm(false);
      setFormData({ company: "", price: "", date: "" });
    } catch (err) {
      console.error("Failed to create IPO:", err);
    }
  };

  return (
    <div className="App">
      <header>
        üìà IPO Tracker
        <div>
          <button className="add-btn" onClick={() => setShowForm(true)}>
            ‚ûï Add IPO
          </button>
          <button
            className="toggle-btn"
            onClick={() => setTheme(theme === "light" ? "dark" : "light")}
          >
            {theme === "light" ? "üåô" : "‚òÄÔ∏è"}
          </button>
        </div>
      </header>

      <div className="ipo-list">
        {ipoList.map((ipo, index) => (
          <div className="ipo-card" key={index}>
            <h3>{ipo.company}</h3>
            <p>Price: ‚Çπ{ipo.price}</p>
            <p>Date: {ipo.date}</p>
          </div>
        ))}
      </div>

      {showForm && (
        <div className="form-popup active">
          <h2>Add New IPO</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="company"
              placeholder="Company Name"
              value={formData.company}
              onChange={handleInputChange}
              required
            />
            <input
              type="number"
              name="price"
              placeholder="Issue Price"
              value={formData.price}
              onChange={handleInputChange}
              required
            />
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
              required
            />
            <button type="submit" className="submit-btn">
              Submit IPO
            </button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
