import React, { useEffect, useState } from "react";
import { fetchIPOs, createIPO } from "../api";  // <-- Corrected path

function IPOList() {
  const [ipos, setIpos] = useState([]);
  const [form, setForm] = useState({
    company_name: "",
    issue_date: "",
    price: "",
    lot_size: "",
    status: "upcoming"
  });

  // Fetch IPO list on page load
  useEffect(() => {
    fetchIPOs().then(setIpos);
  }, []);

  // Submit new IPO
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Send data to API
    const newIPO = await createIPO(form);

    // Update IPO list
    setIpos([...ipos, newIPO]);

    // Reset form
    setForm({
      company_name: "",
      issue_date: "",
      price: "",
      lot_size: "",
      status: "upcoming"
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>IPO List</h2>

      <ul>
        {ipos.map((ipo) => (
          <li key={ipo.id}>
            {ipo.company_name} - {ipo.price} â‚¹ ({ipo.status})
          </li>
        ))}
      </ul>

      <h3>Add IPO</h3>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Company Name"
          value={form.company_name}
          onChange={(e) => setForm({ ...form, company_name: e.target.value })}
        />
        <input
          placeholder="Issue Date (YYYY-MM-DD)"
          value={form.issue_date}
          onChange={(e) => setForm({ ...form, issue_date: e.target.value })}
        />
        <input
          placeholder="Price"
          value={form.price}
          onChange={(e) => setForm({ ...form, price: e.target.value })}
        />
        <input
          placeholder="Lot Size"
          value={form.lot_size}
          onChange={(e) => setForm({ ...form, lot_size: e.target.value })}
        />
        <select
          value={form.status}
          onChange={(e) => setForm({ ...form, status: e.target.value })}
        >
          <option value="upcoming">Upcoming</option>
          <option value="ongoing">Ongoing</option>
          <option value="closed">Closed</option>
        </select>
        <button type="submit">Add IPO</button>
      </form>
    </div>
  );
}

export default IPOList;
