// App.js
import React, { useState, useEffect } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [ipos, setIpos] = useState([]);
  const [formData, setFormData] = useState({
    companyName: "",
    issuePrice: "",
    issuedDate: "",
  });

  useEffect(() => {
    async function loadData() {
      try {
        const data = await fetchIPOs();
        setIpos(data);
      } catch (error) {
        console.error("Error fetching IPOs:", error);
      }
    }
    loadData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.companyName || !formData.issuePrice || !formData.issuedDate) {
      alert("Please fill all fields.");
      return;
    }

    try {
      const newIPO = await createIPO(formData);
      setIpos([...ipos, newIPO]);
      setFormData({ companyName: "", issuePrice: "", issuedDate: "" });
    } catch (error) {
      console.error("Error creating IPO:", error);
    }
  };

  return (
    <div className="App">
      <h1>IPO Dashboard</h1>
      <form className="ipo-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="companyName"
          placeholder="Company Name"
          value={formData.companyName}
          onChange={handleChange}
        />
        <input
          type="number"
          name="issuePrice"
          placeholder="Issue Price"
          value={formData.issuePrice}
          onChange={handleChange}
        />
        <input
          type="date"
          name="issuedDate"
          placeholder="Issued Date"
          value={formData.issuedDate}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>

      <div className="ipo-list">
        {ipos.map((ipo, index) => (
          <div className="ipo-card" key={index}>
            <h3>{ipo.companyName}</h3>
            <p>Issue Price: â‚¹{ipo.issuePrice}</p>
            <p>Issued Date: {ipo.issuedDate}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
