const API_URL = "http://127.0.0.1:8000/api/ipos/";

// Fetch IPO list (GET request)
export async function fetchIPOs() {
  const response = await fetch(API_URL);
  if (!response.ok) {
    throw new Error("Failed to fetch IPOs");
  }
  return response.json();
}

// Create new IPO (POST request) - supports JWT token
export async function createIPO(ipoData, token = null) {
  const headers = {
    "Content-Type": "application/json",
  };

  // If token provided, add Authorization header
  if (token) {
    headers["Authorization"] = `Bearer ${token}`;
  }

  const response = await fetch(API_URL, {
    method: "POST",   // FIXED: Added comma here
    headers: headers,
    body: JSON.stringify(ipoData),
  });

  if (!response.ok) {
    throw new Error("Failed to create IPO");
  }

  return response.json();
}
