import React, { useState, useEffect } from "react";
import { fetchIPOs, createIPO } from "./api";
import "./App.css";

function App() {
  const [ipos, setIpos] = useState([]);
  const [formVisible, setFormVisible] = useState(false);
  const [formData, setFormData] = useState({
    company_name: "",
    issue_date: "",
    price: "",
    lot_size: "",
    status: "",
  });

  // Fetch IPOs when component loads
  useEffect(() => {
    async function loadData() {
      try {
        const data = await fetchIPOs();
        setIpos(data);
      } catch (error) {
        console.error("Error fetching IPOs:", error);
      }
    }
    loadData();
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newIPO = await createIPO(formData);
      setIpos([...ipos, newIPO]);
      setFormVisible(false);
      setFormData({
        company_name: "",
        issue_date: "",
        price: "",
        lot_size: "",
        status: "",
      });
    } catch (error) {
      console.error("Error creating IPO:", error);
    }
  };

  return (
    <div className="app-container">
      <h1 className="title">IPO Dashboard</h1>

      {/* IPO List */}
      <div className="ipo-list">
        {ipos.map((ipo) => (
          <div key={ipo.id} className="ipo-card">
            <h3>{ipo.company_name}</h3>
            <p>Issue Date: {ipo.issue_date}</p>
            <p>Price: â‚¹{ipo.price}</p>
            <p>Lot Size: {ipo.lot_size}</p>
            <p>Status: {ipo.status}</p>
          </div>
        ))}
      </div>

      {/* Add IPO Button */}
      <button
        className="add-btn"
        onClick={() => setFormVisible(!formVisible)}
      >
        {formVisible ? "Close Form" : "Add IPO"}
      </button>

      {/* Add IPO Form (Animated Pop-up) */}
      {formVisible && (
        <form className="ipo-form animate-popup" onSubmit={handleSubmit}>
          <input
            type="text"
            name="company_name"
            placeholder="Company Name"
            value={formData.company_name}
            onChange={handleChange}
            required
          />
          <input
            type="date"
            name="issue_date"
            value={formData.issue_date}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="price"
            placeholder="Price"
            value={formData.price}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="lot_size"
            placeholder="Lot Size"
            value={formData.lot_size}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="status"
            placeholder="Status"
            value={formData.status}
            onChange={handleChange}
            required
          />
          <button type="submit" className="submit-btn glow-btn">
            Submit
          </button>
        </form>
      )}
    </div>
  );
}

export default App;
